mapper.select:
SELECT d.*, c.field, c.field_conf
FROM developer d
         JOIN (
    SELECT *, ROW_NUMBER() OVER (PARTITION BY dev_id ORDER BY field_conf DESC) AS rn
    FROM contribution
    WHERE field = #{field}
) c ON d.dev_id = c.dev_id AND c.rn = 1
ORDER BY d.talent_rank DESC
LIMIT #{limit} OFFSET #{offset};

SELECT d.*, c.field, c.field_conf
FROM developer d
         JOIN (
    SELECT *, ROW_NUMBER() OVER (PARTITION BY dev_id ORDER BY field_conf DESC) AS rn
    FROM contribution
) c ON d.dev_id = c.dev_id AND c.rn = 1
WHERE  d.nation = #{nation}
ORDER BY d.talent_rank DESC
LIMIT #{limit} OFFSET #{offset};

SELECT d.*, c.field, c.field_conf
FROM developer d
         JOIN (
    SELECT *, ROW_NUMBER() OVER (PARTITION BY dev_id ORDER BY field_conf DESC) AS rn
    FROM contribution
    WHERE field = #{field}
) c ON d.dev_id = c.dev_id AND c.rn = 1
WHERE d.nation = #{nation}
ORDER BY d.talent_rank DESC
LIMIT #{limit} OFFSET #{offset};
